  <style lang="scss" scoped>
    #navArea{
      position: relative;
      //這個高度太大會遮蓋router-view產出的頁面元件的hover
      height: 5em;
      .navSpace{
        height: 5em;
        background-color: #000000;
      }
      .headerContainer{
        position: fixed;
        top: 0;
        width: 100vw;
        //不設定這行，裡面兩個inline-block元素上下會有空隙
        display:flex;
        background: #000000dd;
        //用line-height撐開navbar
        line-height: 5em;
        color:#ffffff;
        z-index: 990;
        .logoArea{
          display:inline-block;
          width:50%;
          .logoContainer{
            position:relative;
            left:10%;
            display:inline-block;
            line-height: 5em;
            cursor: pointer;
            >svg{
              //logo點選區塊
              width: 95px;
              //讓svg高度跟Container行高一樣，沒設定預設會是300x150px，太高
              height:5em;
            }
          }
        }
        nav{
          display: inline-block;
          width: 40%;
          .togTapContainer{
            display:none;
          }
          >ul{
            list-style: none;
            display: flex;
            justify-content: space-around;
            >li{
              display: inline-block;
              font-size: 1.4em;
            }
            >li:hover{
              color: #ff5900aa;
              text-shadow: 0em 0em 1em $mainColor;
            }
          }
          #mobNavTog{
            //手機主選單觸發鈕，在電腦版面隱藏
            display:none;
            font-size:1.4em;
          }
          .mobNavInnerPic{
            display: none;
            >img{
              width: 100%;
            }
          }
          @media screen and (max-width: 991px){
            //手機版選單鈕置右
            text-align:right;
            .togTapContainer{
              display:inline-block;
            }
            ul{
              //991px版面以下先隱藏，以主選單觸發紐點擊再顯示
              display: none;
            }
            #mobNavTog{
              //手機主選單觸發鈕，在電腦版面隱藏，991px以下版面打開
              display: inline-block;
              position: relative;
              right: 0;
            }
          }
        }
        @media screen and (max-width: 991px){
          .logoArea{
            text-align:left;
          }
          .mobileNav{
            position:fixed;
            right:0;
            top:0;
            width:50vw;
            height:100vh;
            background:#000000;
            z-index:998;
            text-align:center;
            transition:2s;
            >ul{
              display:flex;
              flex-wrap:wrap;
              >li{
                width: 100%;
              }
            }
            .mobNavInnerPic{
              display: block;
              position: absolute;
              bottom: 0;
              width: 100%;
              >img{
                width: 100%;
              }
            }
          }
        }
      }
      >.topButton{
        display: inline-block;
        position: fixed;
        bottom: 50px;
        right: 20px;
        width: 50px;
        height: 50px;
        cursor: pointer;
        >h1{
          text-align: center;
          >svg{
            scale: 1.2;
          }
        }
      }
      .router-link-active{
        //因為bootstrap的.nav-link-focus類會覆蓋router的樣式，所以需要加入!important
        color:$mainColor !important;
        border-bottom: 2px solid $mainColor;
        font-weight: 600;
        text-shadow: 0em 0em 1em $mainColor;
        transition: 0.5s;
        @media screen and (max-width: 991px){
          border: none;
        }
      }
    }
  </style>
  
  <script>
  import footerArea from '@/components/footerArea.vue'
  
  export default {
    name: 'navArea',
    methods: {
      //手機版主選單開關
      mobNavTog(){
        let navUlContainer = document.getElementById("navUlContainer");
        let mobNavTog = document.getElementById("mobNavTog");
        navUlContainer.classList.toggle("mobileNav");
        mobNavTog.classList.toggle("fa-bars");
        mobNavTog.classList.toggle("fa-xmark");
      },
      //各分頁選項點擊
      navClick(){
        let navUlContainer = document.getElementById("navUlContainer");
        let mobNavTog = document.getElementById("mobNavTog");
        //在手機版主選單被點擊才會更改toggle圖標
        if(navUlContainer.classList.contains("mobileNav")){
          navUlContainer.classList.remove("mobileNav");
          mobNavTog.classList.toggle("fa-bars");
          mobNavTog.classList.toggle("fa-xmark");
        }
      },
      logoClick(){
        this.$router.push({
          name:"home"
        })
      },
      goTop(){
        window.scrollTo({
          top: 0,
          behavior: 'smooth'
        })
      }
    },
    components: {
      footerArea
    }
  }
  </script>